import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileSelect = (event) => {
    setSelectedFile(event.target.files[0]);
    setError(null);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/api/similar', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setRecommendations(data.similar_artworks);
    } catch (err) {
      setError('Error getting recommendations. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Art Recommendation System</h1>
      </header>

      <main className="App-main">
        <section className="upload-section">
          <h2>Upload an Image</h2>
          <div className="upload-container">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              className="file-input"
            />
            <button 
              onClick={handleUpload}
              disabled={!selectedFile || loading}
              className="upload-button"
            >
              {loading ? 'Getting recommendations...' : 'Get Similar Artworks'}
            </button>
          </div>
          {error && <p className="error-message">{error}</p>}
        </section>

        {recommendations.length > 0 && (
          <section className="recommendations-section">
            <h2>Similar Artworks</h2>
            <div className="recommendations-grid">
              {recommendations.map((artwork) => (
                <div key={artwork.id} className="artwork-card">
                  <img 
                    src={artwork.image_url} 
                    alt={artwork.title}
                    className="artwork-image"
                  />
                  <div className="artwork-info">
                    <h3>{artwork.title}</h3>
                    <p className="artist">{artwork.artist}</p>
                    <p className="year">{artwork.year}</p>
                    <p className="description">{artwork.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
